import Card from './Card';
import './name.css';
import Yourname  from './yourname';

function Myname(props){
    const  [title, uptite] = useState(props.title);

    const thefunc = ()=>{
        uptite("Updated");
    }
    return (
    <Card className="gen">
        <Yourname date={props.date}/>
        <div className='tandm'>
            <h2>{title}</h2>
            <div className="mount">
                <h2>{props.amount}</h2>
            </div>
        </div>
        <button onclick = {thefunc}>Update</button>
    
    </Card>);
}
export default Myname;
